    private void saveButtonMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_saveButtonMouseClicked
        // Create output text file and write GUI data to it.
        try {
        FileWriter fstream = new FileWriter("output.txt");
        BufferedWriter out = new BufferedWriter(fstream);
        out.write("   |  Product  | Code |      Description      | Price | Quantity | Subtotal |");
        out.newLine();
        productGUI.setIndex(0);
        while(productGUI.getName() != "") {
            out.write(String.format("    %11s", productGUI.getName()) + String.format(" %6s", productGUI.getCode()) +
                    String.format(" %23s", productGUI.getDescript()) + String.format(" $%6.2f", productGUI.getPrice()) +
                    String.format(" %10d", productGUI.getQuantity()));
            if(productGUI.getQuantity() >= 10) {
                out.write(String.format("  $%8.2f",(productGUI.getQuantity() * productGUI.getPrice() * (1.0 - productGUI.ORDER_10))));
                out.write(" (%" + (100.0*productGUI.ORDER_10 + " DISCOUNT)"));
            }
            else
                out.write(String.format("  $%8.2f",(productGUI.getQuantity() * productGUI.getPrice())));
            out.newLine();
            
            productGUI.incIndex();
        }
        out.write("Total");
        out.newLine();
        out.write("-------");
        out.newLine();
        out.write(taxDisplay.getText());
        out.close();
        }catch (Exception e) {
            System.err.println("Error: " + e.getMessage());
        }
    }//GEN-LAST:event_saveButtonMouseClicked

