        total += Double.parseDouble(subtotalDisplay5.getText().substring(1,subtotalDisplay5.getText().length()));
        total += Double.parseDouble(subtotalDisplay6.getText().substring(1,subtotalDisplay6.getText().length()));
        total += Double.parseDouble(subtotalDisplay7.getText().substring(1,subtotalDisplay7.getText().length()));
        total += Double.parseDouble(subtotalDisplay8.getText().substring(1,subtotalDisplay8.getText().length()));
        total += Double.parseDouble(subtotalDisplay9.getText().substring(1,subtotalDisplay9.getText().length()));
